public class Solution {

    class Node {
        public Character ch;
        public Node next;

        Node(Character ch) {
            this.ch = ch;
        }

        public Node setNext(Node next) {
            this.next = next;
            return next;
        }
    }

    public int longestBeautifulSubstring(String word) {

        Node a = new Node('a');
        a.setNext(new Node('e'))
                .setNext(new Node('i'))
                .setNext(new Node('o'))
                .setNext(new Node('u'));

        int maxLength = 0;

        Node now = null;
        int start = 0;
        int end = 0;

        for (int i = 0; i < word.length(); i ++) {

            if ('a' == word.charAt(i) && now == null) {
                start = i;
                now = a;
                continue;
            }

            if (now == null) {
                continue;
            }

            if (i == word.length() - 1 && now.next == null && now.ch.equals(word.charAt(i)) ) {
                end = i;
                maxLength = Math.max(maxLength, end - start + 1);
            }

            if (now.ch.equals(word.charAt(i))) {
                continue;
            }

            if ( now.next == null ) {
                end = i - 1;
                maxLength = Math.max(maxLength, end - start + 1);
                now = null;
            } else if ( now.next.ch.equals(word.charAt(i)) ) {
                now = now.next;
                if (i == word.length() - 1 && now.next == null ) {
                    end = i;
                    maxLength = Math.max(maxLength, end - start + 1);
                }
            } else {
                now = null;
            }

            if ( 'a' == word.charAt(i) && now == null ) {
                start = i;
                now = a;
            }

        }

        return maxLength;
    }


    public static void main(String[] args) {
        System.out.println(new Solution().longestBeautifulSubstring("aeiouue"));
//        System.out.println(new Solution().longestBeautifulSubstring(""));
//        System.out.println(new Solution().longestBeautifulSubstring(""));

    }
}
